ValidationRule.errorConditionFormula:
IF( AND( OR(AND( ISPICKVAL(PRIORVALUE(Status),'Canceled'),ISPICKVAL(Is_Processed__c,'2')), ISPICKVAL(PRIORVALUE(Status),'Completed') ), ISCHANGED(Status)
), True, False ) || IF( AND( AND( ISPICKVAL(PRIORVALUE(Status),'On-Hold'), ISPICKVAL(Status, 'Completed')
), ISCHANGED(Status) ), True, False )
